import React, { useState } from 'react';
import './css/FramePage.css';

function FramePage() {
  const [address, setAddress] = useState('');
  const [inputValues, setInputValues] = useState({
    initialData: '',
    floorCount: '',
    firstFloor: '',
    floorHeight: '',
    outerWallPerimeter: '',
    baseArea: '',
    outerWallThickness: '',
    outerWallCladding: '',
    paroHydroInsulation: '',
    windProtection: '',
    insulation: '',
    innerWallLength: '',
    interiorWallThickness: '',
    osb: '',
    heightWindow: '',
    widthWindow: '',
    sumWindow: '',
    heightDoorOuter: '',
    widthDoorOuter: '',
    sumDoorOuter: '',
    heightDoorInner: '',
    widthDoorInner: '',
    sumDoorInner: '',
    floorthickness: '',
  });
  const [doorWindows, setDoorWindows] = useState([{ height: '', width: '', count: '' }]);
  const [outerDoors, setOuterDoors] = useState([{ height: '', width: '', count: '' }]);
  const [innerDoors, setInnerDoors] = useState([{ height: '', width: '', count: '' }]);
  const [floorCount, setFloorCount] = useState(1);

  const client = {
    name: 'Тестовый Тест Тестов',
    address: 'ул.Тестовая ул., дом 43-45',
    phone: 'тел. 8-900-000-00-00',
  };

  const handleDoorWindowChange = (index, name, value) => {
    const updatedDoorWindows = [...doorWindows];
    updatedDoorWindows[index][name] = value;
    setDoorWindows(updatedDoorWindows);
  };

  const handleOuterDoorChange = (index, name, value) => {
    const updatedOuterDoors = [...outerDoors];
    updatedOuterDoors[index][name] = value;
    setOuterDoors(updatedOuterDoors);
  };

  const handleInnerDoorChange = (index, name, value) => {
    const updatedInnerDoors = [...innerDoors];
    updatedInnerDoors[index][name] = value;
    setInnerDoors(updatedInnerDoors);
  };

  const handleAddDoorWindow = () => {
    setDoorWindows([...doorWindows, { height: '', width: '', count: '' }]);
  };

  const handleAddOuterDoor = () => {
    setOuterDoors([...outerDoors, { height: '', width: '', count: '' }]);
  };

  const handleAddInnerDoor = () => {
    setInnerDoors([...innerDoors, { height: '', width: '', count: '' }]);
  };

  const handleSave = () => {
    // Ваша функция сохранения
  };

  const handleClear = () => {
    setAddress('');
    setInputValues({
      initialData: '',
      floorCount: '',
      firstFloor: '',
      floorHeight: '',
      outerWallPerimeter: '',
      baseArea: '',
      outerWallThickness: '',
      outerWallCladding: '',
      paroHydroInsulation: '',
      windProtection: '',
      insulation: '',
      innerWallLength: '',
      interiorWallThickness: '',
      osb: '',
      heightWindow: '',
      widthWindow: '',
      sumWindow: '',
      heightDoorOuter: '',
      widthDoorOuter: '',
      sumDoorOuter: '',
      heightDoorInner: '',
      widthDoorInner: '',
      sumDoorInner: '',
      floorthickness: '',
    });
  };

  const handleClose = () => {
    window.location.href = '/';
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setInputValues({
      ...inputValues,
      [name]: value,
    });
  };

  const handleAddFloors = () => {
    // Увеличиваем количество этажей
    setFloorCount(floorCount + 1);
  
    // Создаем копию текущих значений
    const newInputValues = { ...inputValues };
  
    // Добавляем новый объект для нового этажа
    newInputValues[`floor${floorCount + 1}`] = {
      firstFloor: '',
      floorHeight: '',
      outerWallPerimeter: '',
      baseArea: '',
      outerWallThickness: '',
      outerWallCladding: '',
      paroHydroInsulation: '',
      windProtection: '',
      insulation: '',
      innerWallLength: '',
      interiorWallThickness: '',
      osb: '',
      heightWindow: '',
      widthWindow: '',
      sumWindow: '',
      heightDoorOuter: '',
      widthDoorOuter: '',
      sumDoorOuter: '',
      heightDoorInner: '',
      widthDoorInner: '',
      sumDoorInner: '',
      floorthickness: '',
    };
  
    // Обновляем состояние
    setInputValues(newInputValues);
  };
  
  
  

  return (
    <body>
      <header>
        <div className="name">
          <h1>{client.name}</h1>
          <p>{client.address}</p>
          <p>{client.phone}</p>
        </div>
        <button className="close-button" onClick={handleClose}>X</button>
      </header>
      <div className="container">
        <form className="form1">
          <h2 className="title">Каркас</h2>
          <div style={{ display: 'flex', marginBottom: '10px' }}>
            <input
              type="text"
              className="address-input"
              placeholder="Добавить адрес объекта строительства"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <button className="button" onClick={handleSave}>Сохранить</button>
            <button className="button" onClick={handleClear}>Очистить</button>
          </div>
          <div className="input-row">
            <label className="label1">Исходные данные:</label>
          </div>
          <div className="input-row">
            <label className="label">Количество этажей:</label>
            <input
              type="number"
              name="floorCount"
              value={inputValues.floorCount}
              onChange={handleInputChange}
            />
            <button className="button-confirm" type="button" onClick={handleAddFloors}>Подтвердить</button>
          </div>
          <div className="input-row">
            <label className="label">Первый этаж:</label>
            <input
              type="text"
              name="firstFloor"
              value={inputValues.firstFloor}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-row">
            <label className="label">Высота этажа:</label>
            <input
              type="number"
              name="floorHeight"
              value={inputValues.floorHeight}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-row">
            <label className="label">Периметр наружных стен:</label>
            <input
              type="number"
              name="outerWallPerimeter"
              value={inputValues.outerWallPerimeter}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-row">
            <label className="label">Площадь основания:</label>
            <input
              type="number"
              name="baseArea"
              value={inputValues.baseArea}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-row">
            <label className="label">Толщина внешних стен:</label>
            <select
              name="outerWallThickness"
              value={inputValues.outerWallThickness}
              onChange={handleInputChange}
            >
              <option value="Выбор большая">большая</option>
              <option value="Выбор средняя">средняя</option>
              <option value="Выбор маленькая">маленькая</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Длина внутренних стен:</label>
            <input
              type="number"
              name="innerWallLength"
              value={inputValues.innerWallLength}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-row">
            <label className="label">Толщина внутренних стен:</label>
            <select
              name="interiorWallThickness"
              value={inputValues.interiorWallThickness}
              onChange={handleInputChange}
            >
              <option value="Выбор большая">большая</option>
              <option value="Выбор средняя">средняя</option>
              <option value="Выбор маленькая">маленькая</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label1">Обшивка внешних стен:</label>
          </div>
          <div className="input-row">
            <label className="label">ОСБ:</label>
            <select
              name="osb"
              value={inputValues.osb}
              onChange={handleInputChange}
            >
              <option value="Выбор 1">1</option>
              <option value="Выбор 2">2</option>
              <option value="Выбор 3">3</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Парогидроизоляция:</label>
            <select
              name="paroHydroInsulation"
              value={inputValues.paroHydroInsulation}
              onChange={handleInputChange}
            >
              <option value="Выбор 1">1</option>
              <option value="Выбор 2">2</option>
              <option value="Выбор 3">3</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Ветрозащита:</label>
            <select
              name="windProtection"
              value={inputValues.windProtection}
              onChange={handleInputChange}
            >
              <option value="Выбор 1">1</option>
              <option value="Выбор 2">2</option>
              <option value="Выбор 3">3</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Утеплитель:</label>
            <select
              name="insulation"
              value={inputValues.insulation}
              onChange={handleInputChange}
            >
              <option value="Выбор 1">1</option>
              <option value="Выбор 2">2</option>
              <option value="Выбор 3">3</option>
            </select>
          </div>
          {/* Оконные проемы */}
          <div className="input-row">
            <label className="label1">Оконные проёмы:</label>
          </div>
          <div className="input-row">
            <label className="label2">Высота</label>
            <label className="label2">Ширина</label>
            <label className="label2">Количество</label>
          </div>
          {doorWindows.map((doorWindow, index) => (
            <div className="input-row" key={`doorWindow_${index}`}>
              <input
                type="number"
                value={doorWindow.height}
                onChange={(e) => handleDoorWindowChange(index, 'height', e.target.value)}
              />
              <input
                type="number"
                value={doorWindow.width}
                onChange={(e) => handleDoorWindowChange(index, 'width', e.target.value)}
              />
              <input
                type="number"
                value={doorWindow.count}
                onChange={(e) => handleDoorWindowChange(index, 'count', e.target.value)}
              />
            </div>
          ))}
          <button className="button" type="button" onClick={handleAddDoorWindow}>+</button>
          {/* Внешние дверные проемы */}
          <div className="input-row">
            <label className="label1">Дверные проёмы внешние:</label>
          </div>
          <div className="input-row">
            <label className="label2">Высота</label>
            <label className="label2">Ширина</label>
            <label className="label2">Количество</label>
          </div>
          {outerDoors.map((outerDoor, index) => (
            <div className="input-row" key={`outerDoor_${index}`}>
              <input
                type="number"
                value={outerDoor.height}
                onChange={(e) => handleOuterDoorChange(index, 'height', e.target.value)}
              />
              <input
                type="number"
                value={outerDoor.width}
                onChange={(e) => handleOuterDoorChange(index, 'width', e.target.value)}
              />
              <input
                type="number"
                value={outerDoor.count}
                onChange={(e) => handleOuterDoorChange(index, 'count', e.target.value)}
              />
            </div>
          ))}
          <button className="button" type="button" onClick={handleAddOuterDoor}>+</button>
          {/* Внутренние дверные проемы */}
          <div className="input-row">
            <label className="label1">Дверные проёмы внутренние:</label>
          </div>
          <div className="input-row">
            <label className="label2">Высота</label>
            <label className="label2">Ширина</label>
            <label className="label2">Количество</label>
          </div>
          {innerDoors.map((innerDoor, index) => (
            <div className="input-row" key={`innerDoor_${index}`}>
              <input
                type="number"
                value={innerDoor.height}
                onChange={(e) => handleInnerDoorChange(index, 'height', e.target.value)}
              />
              <input
                type="number"
                value={innerDoor.width}
                onChange={(e) => handleInnerDoorChange(index, 'width', e.target.value)}
              />
              <input
                type="number"
                value={innerDoor.count}
                onChange={(e) => handleInnerDoorChange(index, 'count', e.target.value)}
              />
            </div>
          ))}
          <button className="button" type="button" onClick={handleAddInnerDoor}>+</button>
          <div className="input-row">
            <label className="label">Толщина перекрытия:</label>
            <input
              type="number"
              name="floorthickness"
              value={inputValues.floorthickness}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-row">
            <label className="label">ОСБ</label>
            <select
              name="osb"
              value={inputValues.osb}
              onChange={handleInputChange}
            >
              <option value="Выбор большая">большая</option>
              <option value="Выбор средняя">средняя</option>
              <option value="Выбор маленькая">маленькая</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Парогидроизоляция:</label>
            <select
              name="paroHydroInsulation"
              value={inputValues.paroHydroInsulation}
              onChange={handleInputChange}
            >
              <option value="Выбор большая">большая</option>
              <option value="Выбор средняя">средняя</option>
              <option value="Выбор маленькая">маленькая</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Ветрозащита:</label>
            <select
              name="windProtection"
              value={inputValues.windProtection}
              onChange={handleInputChange}
            >
              <option value="Выбор большая">большая</option>
              <option value="Выбор средняя">средняя</option>
              <option value="Выбор маленькая">маленькая</option>
            </select>
          </div>
          <div className="input-row">
            <label className="label">Утеплитель:</label>
            <select
              name="insulation"
              value={inputValues.insulation}
              onChange={handleInputChange}
            >
              <option value="Выбор большая">большая</option>
              <option value="Выбор средняя">средняя</option>
              <option value="Выбор маленькая">маленькая</option>
            </select>
          </div>
          <button className='btn_save' onClick={handleSave}>Сохранить расчет</button>
        </form>
      </div>
    </body>
  );
}

export default FramePage;
