import React, { useState } from 'react';
import './css/LoginPage.css'; 

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = (event) => {
    event.preventDefault();
    const passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
  
    if (!passwordPattern.test(password)) {
      setError('Пароль должен содержать не менее 8 символов, хотя бы одну цифру, строчные и заглавные буквы, и хотя бы один спецсимвол');
      return;
    }
  
    if (!/^[a-zA-Z]+$/.test(username)) {
      setError('Логин должен содержать только буквы');
      return;
    }
  
    // Здесь будет дополнительная логика проверки
    // Ваш код проверки пароля


    if (password === 'You123' && username === "You123") {
      window.location.href = '/clients';
    } else {
      setError('Пароль неверный');
    }
  };
  

  return (
    <div className="container">
      <form className="form" onSubmit={handleLogin}>
        <h2 className="title">АВТОРИЗАЦИЯ</h2>
        <label>
          Логин:
          <input
            className="input"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <label>
          Пароль:
          <input
            className="input"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        {error && <p className="error">{error}</p>}
        <input className="button" type="submit" value="ВХОД" />
      </form>
      <input className="button" type="submit" value="РЕГИСТРАЦИЯ" />

    </div>
  );
}

export default LoginPage;
