import React, { useState } from 'react';
import './css/RegistrationPage.css'; // Подключаем CSS файл 

function RegistrationPage() {
  const [lastName, setLastName] = useState('');
  const [firstName, setFirstName] = useState('');
  const [middleName, setMiddleName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({
    lastName: '',
    firstName: '',
    email: '',
    phone: '',
    address: '',
    password: ''
  });

  const handleRegistration = (event) => {
    event.preventDefault();

    // Проверка Фамилии
    if (!/^[\u0410-\u042f\u0430-\u044f]+$/.test(lastName)) {
      setErrors(prevErrors => ({ ...prevErrors, lastName: 'Фамилия должна быть написана на русском одним словом' }));
    }

    // Проверка Имени
    if (!/^[\u0410-\u042f\u0430-\u044f]+$/.test(firstName)) {
      setErrors(prevErrors => ({ ...prevErrors, firstName: 'Имя должно быть написано на русском одним словом' }));
    }

    // Проверка Email
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      setErrors(prevErrors => ({ ...prevErrors, email: 'Email должен быть в формате example@mail.com' }));
    }

    // Проверка Телефона
    if (!/^79\d{9}$/.test(phone)) {
      setErrors(prevErrors => ({ ...prevErrors, phone: 'Телефон должен начинаться с 79 и содержать 11 цифр' }));
    }

    // Проверка Пароля
    if (!/^\d{6}$/.test(password)) {
      setErrors(prevErrors => ({ ...prevErrors, password: 'Пароль должен состоять из 6 цифр' }));
    }

    // Ваша логика регистрации здесь
  };

  const getInputClass = (inputValue) => {
    return inputValue ? 'input' : 'input error';
  };

  return (
    <div className="container">
      <form className="form" onSubmit={handleRegistration}>
        <h2 className="title">РЕГИСТРАЦИЯ</h2>
        <div className="input-container">
          <label>Фамилия:</label>
          <input
            className={getInputClass(lastName)}
            type="text"
            value={lastName}
            onChange={(e) => { setErrors({ ...errors, lastName: '' }); setLastName(e.target.value); }}
            placeholder="Введите фамилию"
          />
          {errors.lastName && <p className="error">{errors.lastName}</p>}
        </div>
        <div className="input-container">
          <label>Имя:</label>
          <input
            className={getInputClass(firstName)}
            type="text"
            value={firstName}
            onChange={(e) => { setErrors({ ...errors, firstName: '' }); setFirstName(e.target.value); }}
            placeholder="Введите имя"
          />
          {errors.firstName && <p className="error">{errors.firstName}</p>}
        </div>
        <div className="input-container">
          <label>Отчество (при наличии):</label>
          <input
            className="input"
            type="text"
            value={middleName}
            onChange={(e) => setMiddleName(e.target.value)}
            placeholder="Введите отчество"
          />
        </div>
        <div className="input-container">
          <label>Телефон:</label>
          <input
            className={getInputClass(phone)}
            type="tel"
            value={phone}
            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
            onChange={(e) => { setErrors({ ...errors, phone: '' }); setPhone(e.target.value); }}
            placeholder="Введите номер телефона"
          />
          {errors.phone && <p className="error">{errors.phone}</p>}
        </div>
        <div className="input-container">
          <label>Email:</label>
          <input
            className={getInputClass(email)}
            type="email"
            value={email}
            onChange={(e) => { setErrors({ ...errors, email: '' }); setEmail(e.target.value); }}
            placeholder="example@mail.com"
          />
          {errors.email && <p className="error">{errors.email}</p>}
        </div>
        <div className="input-container">
          <label>Адрес:</label>
          <input
            className={getInputClass(address)}
            type="text"
            value={address}
            onChange={(e) => { setErrors({ ...errors, address: '' }); setAddress(e.target.value); }}
            placeholder="Введите адрес"
          />
          {errors.address && <p className="error">{errors.address}</p>}
        </div>
        <div className="input-container">
          <label>Пароль:</label>
          <input
            className={getInputClass(password)}
            type="password"
            value={password}
            onChange={(e) => { setErrors({ ...errors, password: '' }); setPassword(e.target.value); }}
            placeholder="Введите пароль"
          />
          {errors.password && <p className="error">{errors.password}</p>}
        </div>
        <input className="button" type="submit" value="РЕГИСТРАЦИЯ" />
      </form>
    </div>
  );
}

export default RegistrationPage;
