import React, { useState } from 'react';
import './css/FramePage.css'; // Подключаем файл стилей

function FramePage() {
  const [address, setAddress] = useState('');
  const [inputValues, setInputValues] = useState({
    // Другие поля ввода...
    windowProems: [], // Массив объектов с данными о оконных проемах
    outerDoorProems: [], // Массив объектов с данными о дверных проемах внешних
    innerDoorProems: [], // Массив объектов с данными о дверных проемах внутренних
  });

  const handleSave = () => {
    // Ваша логика сохранения
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setInputValues({
      ...inputValues,
      [name]: value,
    });
  };

  const handleAddProem = (type) => {
    setInputValues({
      ...inputValues,
      [type]: [...inputValues[type], { height: '', width: '', quantity: '' }],
    });
  };

  const handleProemChange = (index, type, field, value) => {
    const updatedProems = [...inputValues[type]];
    updatedProems[index][field] = value;
    setInputValues({
      ...inputValues,
      [type]: updatedProems,
    });
  };

  return (
    <body>
      {/* Ваш код заголовка и кнопки закрытия */}
      <div className="container">
        <form className="form1">
          {/* Ваши поля ввода до проемов */}
          {/* Кнопка для добавления оконных проемов */}
          <button className="button" onClick={() => handleAddProem('windowProems')}>+</button>
          {/* Поля для ввода оконных проемов */}
          {inputValues.windowProems.map((proem, index) => (
            <div className="input-row" key={index}>
              <input
                type="number"
                value={proem.height}
                onChange={(e) => handleProemChange(index, 'windowProems', 'height', e.target.value)}
              />
              <input
                type="number"
                value={proem.width}
                onChange={(e) => handleProemChange(index, 'windowProems', 'width', e.target.value)}
              />
              <input
                type="number"
                value={proem.quantity}
                onChange={(e) => handleProemChange(index, 'windowProems', 'quantity', e.target.value)}
              />
            </div>
          ))}
          {/* Аналогично для дверных проемов внешних и внутренних */}
          {/* Кнопка для добавления дверных проемов внешних */}
          <button className="button" onClick={() => handleAddProem('outerDoorProems')}>+</button>
          {/* Поля для ввода дверных проемов внешних */}
          {inputValues.outerDoorProems.map((proem, index) => (
            <div className="input-row" key={index}>
              <input
                type="number"
                value={proem.height}
                onChange={(e) => handleProemChange(index, 'outerDoorProems', 'height', e.target.value)}
              />
              <input
                type="number"
                value={proem.width}
                onChange={(e) => handleProemChange(index, 'outerDoorProems', 'width', e.target.value)}
              />
              <input
                type="number"
                value={proem.quantity}
                onChange={(e) => handleProemChange(index, 'outerDoorProems', 'quantity', e.target.value)}
              />
            </div>
          ))}
          {/* Кнопка для добавления дверных проемов внутренних */}
          <button className="button" onClick={() => handleAddProem('innerDoorProems')}>+</button>
          {/* Поля для ввода дверных проемов внутренних */}
          {inputValues.innerDoorProems.map((proem, index) => (
            <div className="input-row" key={index}>
              <input
                type="number"
                value={proem.height}
                onChange={(e) => handleProemChange(index, 'innerDoorProems', 'height', e.target.value)}
              />
              <input
                type="number"
                value={proem.width}
                onChange={(e) => handleProemChange(index, 'innerDoorProems', 'width', e.target.value)}
              />
              <input
                type="number"
                value={proem.quantity}
                onChange={(e) => handleProemChange(index, 'innerDoorProems', 'quantity', e.target.value)}
              />
            </div>
          ))}
          {/* Ваши кнопки сохранения и другие поля ввода */}
        </form>
      </div>
    </body>
  );
}

export default FramePage;
